function initialiseShaders() {
    if(textureMod){
        var fragmentShaderSource = '\
        varying mediump vec4 color; \
        varying mediump vec2 texCoord;\
        uniform sampler2D sampler2d; \
        void main(void) \
        { \
            gl_FragColor = texture2D(sampler2d, texCoord) \
            * texture2D(sampler2d, texCoord); \
        }';
    }
    else {
        var fragmentShaderSource = '\
        varying mediump vec4 color; \
        varying mediump vec2 texCoord;\
        uniform sampler2D sampler2d; \
        void main(void) \
        { \
            gl_FragColor = 1.0 * color;\
        }';
    }

    gl.fragShader = gl.createShader(gl.FRAGMENT_SHADER);
    gl.shaderSource(gl.fragShader, fragmentShaderSource);
    gl.compileShader(gl.fragShader);
    if (!gl.getShaderParameter(gl.fragShader, gl.COMPILE_STATUS)) {
        alert("Failed to compile the fragment shader.\n" + gl.getShaderInfoLog(gl.fragShader));
        return false;
    }

    var vertexShaderSource = '\
    attribute highp vec3 myVertex; \
    attribute highp vec4 myColor; \
    attribute highp vec2 myUV; \
    attribute highp vec3 myNormal; \
    uniform mediump mat4 Pmatrix; \
    uniform mediump mat4 Vmatrix; \
    uniform mediump mat4 Mmatrix; \
    uniform mediump mat4 Nmatrix; \
    varying mediump vec4 color; \
    varying mediump vec2 texCoord;\
    void main(void)  \
    { \
        vec4 nN;\
        vec4 v1,v2,v3,v4;\
        vec3 v5;\
        v1 = Mmatrix*vec4(myVertex,1.0);\
        v1.xyz = v1.xyz / v1.w;\
        v2 = Mmatrix*vec4(myVertex+myNormal, 1.0);\
        v2.xyz = v2.xyz / v2.w;\
        v3 = v2 - v1;\
        v5 = normalize(v3.xyz);\
        gl_Position = Pmatrix*Vmatrix*Mmatrix*vec4(myVertex, 1.0);\
        nN = Mmatrix * vec4(myNormal,1.0);\
            /* \
        if (gl_Position.w != 0.0) \
            gl_Position.x /= gl_Position.w; \
        gl_Position.x += 1.0; \
        if (gl_Position.w != 1.0) \
            gl_Position.x *= gl_Position.w; */ \
        color = 0.2* myColor * vec4(0.8,0.8,0.8,1.0) * 0.5 * dot(v5, vec3(1,1,1) + 1.0);\
        color.a = 1.0;\
        texCoord = myUV; \
    }';

    gl.vertexShader = gl.createShader(gl.VERTEX_SHADER);
    gl.shaderSource(gl.vertexShader, vertexShaderSource);
    gl.compileShader(gl.vertexShader);
    if (!gl.getShaderParameter(gl.vertexShader, gl.COMPILE_STATUS)) {
        alert("Failed to compile the vertex shader.\n" + gl.getShaderInfoLog(gl.vertexShader));
        return false;
    }

    gl.programObject = gl.createProgram();

    // Attach the fragment and vertex shaders to it
    gl.attachShader(gl.programObject, gl.fragShader);
    gl.attachShader(gl.programObject, gl.vertexShader);

    // Bind the custom vertex attribute "myVertex" to location 0
    gl.bindAttribLocation(gl.programObject, 0, "myVertex");
    gl.bindAttribLocation(gl.programObject, 1, "myColor");
	gl.bindAttribLocation(gl.programObject, 2, "myUV");
	gl.bindAttribLocation(gl.programObject, 3, "myNormal");

    // Link the program
    gl.linkProgram(gl.programObject);

    if (!gl.getProgramParameter(gl.programObject, gl.LINK_STATUS)) {
        alert("Failed to link the program.\n" + gl.getProgramInfoLog(gl.programObject));
        return false;
    }

    gl.useProgram(gl.programObject);

    return testGLError("initialiseShaders");
}

// FOV, Aspect Ratio, Near, Far 
function get_projection(angle, a, zMin, zMax) {
    var ang = Math.tan((angle*.2)*Math.PI/180);//angle*.5
    return [
    	0.5/ang, 0 , 0, 0,
        0, 0.5*a/ang, 0, 0,
        0, 0, -(zMax+zMin)/(zMax-zMin), -1,
        0, 0, (-2*zMax*zMin)/(zMax-zMin), 0 ];
}
			
